"""
weights: a list of weights for each item
values: a list of values for each item
max_weight: maximum weight the knapsack can carry
"""
n = len(weights)
K = [[0 for x in range(max_weight + 1)] for x in range(n + 1)]

for i in range(n + 1):
    for w in range(max_weight + 1):
        if i == 0 or w == 0:
            K[i][w] = 0
        elif weights[i-1] <= w:
            K[i][w] = max(values[i-1] + K[i-1][w-weights[i-1]],  K[i-1][w])
        else:
            K[i][w] = K[i-1][w]

return K[n][max_weight] if n > 0 else 0

def quick_Sort_helper(nums, low, high):
    if low < high:
        partition_index = partition(nums, low, high)
        quick_Sort_helper(nums, low, partition_index-1)
        quick_Sort_helper(nums, partition_index+1, high)

def partition(nums, low, high):
    pivot = nums[high]
    i = low - 1
    for j in range(low, high):
        if nums[j] < pivot:
            i += 1
            nums[i], nums[j] = nums[j], nums[i]
    nums[i+1], nums[high] = nums[high], nums[i+1]
    return i+1

quick_Sort_helper(nums, 0, len(nums)-1)
