"""
Implement a binary search on a given array.
"""
def binary_search_recursive(arr, target, lower_bound, upper_bound):
    if lower_bound > upper_bound:
        return -1

    mid_index = (lower_bound + upper_bound) // 2 
    mid_elem = arr[mid_index] 

    if target == mid_elem:  
        return mid_index 
    elif mid_elem > target:  
        return binary_search_recursive(arr, target, lower_bound, mid_index - 1) 
    else : 
        return binary_search_recursive(arr, target, mid_index + 1, upper_bound)

return binary_search_recursive(arr, target, 0, len(arr) - 1)

