"""
Finds the closest distance between two points in a list of points.
"""
def distance(point1, point2):
    return ((point1[0]-point2[0])**2 + (point1[1]-point2[1])**2)**0.5

def stripClosest(strip, size, d):
    for i in range(size):
        for j in range(i+1, size):
            if abs(strip[j][1] - strip[i][1]) < d:
                d = distance(strip[i], strip[j])
    return d

def closestUtil(points, n):
    if n <= 3:
        return bruteForce(points, n)

    mid = n // 2
    midPoint = points[mid]

    dl = closestUtil(points[:mid], mid)
    dr = closestUtil(points[mid:], n-mid)

    d = min(dl, dr)

    strip = []
    for point in points:
        if abs(point[0] - midPoint[0]) < d:
            strip.append(point)

    return min(d, stripClosest(strip, len(strip), d))

def bruteForce(points, n):
    min_dist = float("inf")
    for i in range(n-1):
        for j in range(i+1, n):
            dist = distance(points[i], points[j])
            if dist < min_dist:
                min_dist = dist
    return min_dist


