def partition(data, start, end, compare):
    pivot = data[start]
    left = start + 1
    right = end
    while True:
        while left <= right and compare(data[right], pivot) >= 0:
            right = right - 1
        while left <= right and compare(data[left], pivot) <= 0:
            left = left + 1
        if left <= right:
            data[left], data[right] = data[right], data[left]
        else:
            break
    data[start], data[right] = data[right], data[start]
    return right


