"""
Returns the inverse of a given matrix.
"""
if not isinstance(matrix, list):
    raise TypeError("matrix must be a list of lists")
if not all(isinstance(row, list) for row in matrix):
    raise TypeError("matrix must be a list of lists")
if not all(len(row) == len(matrix) for row in matrix):
    raise ValueError("matrix must be a non-empty square matrix")
if not all(len(row) == len(matrix[0]) for row in matrix):
    raise ValueError("matrix must be a non-empty square matrix")
if len(matrix) == 1:
    return [[1 / matrix[0][0]]]
if len(matrix) == 2:
    determinant = matrix[0][0] * matrix[1][1] - matrix[0][1] * matrix[1][0]
    if determinant == 0:
        return False
    else:
        inverse_matrix = [[matrix[1][1] / determinant, -1 * matrix[0][1] / determinant], 
                          [-1 * matrix[1][0] / determinant,  matrix[0][0] / determinant]]
        return inverse_matrix
determinant = 0
for i in range(len(matrix)):
    determinant += ((-1) ** i) * matrix[0][i] * determinant([row[:i] + row[i+1:] for row in matrix[1:]])
if determinant == 0:
    return False
else:
    cofactors = []
    for r in range(len(matrix)):
        cofactor_row = []
        for c in range(len(matrix)):
            minor = [row[:c] + row[c+1:] for row in (matrix[:r] + matrix[r+1:])]
            cofactor_row.append(((-1) ** (r+c)) * determinant(minor))
        cofactors.append(cofactor_row)
    cofactors = transpose(cofactors)
    for i in range(len(cofactors)):
        for j in range(len(cofactors)):
            cofactors[i][j] = cofactors[i][j] / determinant
    return cofactors

