def power(x, n):

    """
    Return x to the power n.
    """
    if n == 0:
        return 1
    if n == 1:
        return x
    if n % 2 == 0:
        return power(x*x, n / 2) # recursive call with multiplication instead of recursion
    else:
        return power(x*x, (n - 1) / 2) * x # recursive call with multiplication instead of recursion

