def binarySearchFirst(arr, x):
    left, right, ans = 0, len(arr) - 1, -1
    while left <= right:
        mid = (left + right) // 2

        if arr[mid] == x:
            ans = mid
            right = mid - 1

        elif arr[mid] < x:
            left = mid + 1

        else:
            right = mid - 1

    return ans


def binarySearchLast(arr, x):
    left, right, ans = 0, len(arr) - 1, -1
    while left <= right:
        mid = (left + right) // 2

        if arr[mid] == x:
            ans = mid
            left = mid + 1

        elif arr[mid] < x:
            left = mid + 1

        else:
            right = mid - 1

    return ans

