def quick_select(arr, low, high, k):
    if low == high:
        return arr[low]
    pivot_pos = partition(arr, low, high)
    left_size = pivot_pos - low + 1
    if k == left_size:
        return arr[pivot_pos]
    elif k < left_size:
        return quick_select(arr, low, pivot_pos-1, k)
    else:
        return quick_select(arr, pivot_pos+1, high, k-left_size)
def findKthSmallest(arr, k):
