def evaluate_postfix(exp):
    """ 
    Evaluate the given postfix expression 

    Parameters:
        exp (str): Postfix expression to evaluate

    Returns:
        int: Evaluated result of the postfix expression
    """
    stack = []

    for i in exp:
        if i.isdigit():
            stack.append(int(i))
        else:
            op1 = stack.pop()
            op2 = stack.pop()

            if i == "+":
                stack.append(op2 + op1)

            elif i == "-":
                stack.append(op2 - op1)

            elif i == "*":
                stack.append(op2 * op1)

            elif i == "/":
                stack.append(op2 / op1)

            elif i == "^":
                stack.append(op2 ** op1)

    return int(stack.pop())

