"""
Convert a hexadecimal number to its base-10 equivalent.
"""
hex_num = hex_num.upper()
hex_num = hex_num.replace(' ', '')
decimal_num = 0
for i in range(len(hex_num)-1, -1, -1):
    if hex_num[i] not in '0123456789ABCDEF':
        raise ValueError('Invalid hexadecimal character.')
    digit = hex_num[i]
    if digit == 'A':
        decimal_num += 10 * (16 ** (len(hex_num)-1-i))
    elif digit == 'B':
        decimal_num += 11 * (16 ** (len(hex_num)-1-i))
    elif digit == 'C':
        decimal_num += 12 * (16 ** (len(hex_num)-1-i))
    elif digit == 'D':
        decimal_num += 13 * (16 ** (len(hex_num)-1-i))
    elif digit == 'E':
        decimal_num += 14 * (16 ** (len(hex_num)-1-i))
    elif digit == 'F':
        decimal_num += 15 * (16 ** (len(hex_num)-1-i))
    else:
        decimal_num += int(digit) * (16 ** (len(hex_num)-1-i))
return decimal_num

