    """
    Sort the array using Bogosort algorithm.
    """
    n = len(arr)
    if n == 1:
        return arr
    else:
        return sort_helper(arr, 0, n-1)
def sort_helper(arr, l, r):
    if l < r:
        pivot = partition(arr, l, r)
        sort_helper(arr, l, pivot-1)
        sort_helper(arr, pivot+1, r)
    return arr

def partition(arr, l, r):
    pivot = arr[r]
    i = l - 1
    for j in range(l, r):
        if arr[j] < pivot:
            i += 1
            arr[i], arr[j] = arr[j], arr[i]
    arr[i+1], arr[r] = arr[r], arr[i+1]
    return i+1

