"""
:param time: list of time
:param distance: list of distance
:return: list of speed
"""
if len(time) != len(distance):
    raise ValueError("The length of time and distance should be equal.")
if len(time) == 0:
    raise ValueError("The length of time and distance should be greater than 0.")
if len(time) == 1:
    return [distance[0] / time[0]]
speed = []
for i in range(len(time) - 1):
    if time[i + 1] - time[i] > 0:
        speed.append((distance[i + 1] - distance[i]) / (time[i + 1] - time[i]))
return speed

"""
:param arr: array of integers
:return: tuple of (index of max, index of min)
"""
if len(arr) == 0:
    return None

max_index = 0
min_index = 0

for i in range(1, len(arr)):
    if arr[i] > arr[max_index]:
        max_index = i
    if arr[i] < arr[min_index]:
        min_index = i

return (max_index, min_index)

