def longestSubsetWithSameSign(arr, n):

    if n == 0:
        return 0
    if n == 1:
        return 1
    arr.sort()
    dp = [[0 for i in range(n)] for j in range(n)]
    for i in range(n):
        dp[i][i] = 1
    for i in range(n-1):
        if arr[i] == arr[i+1]:
            dp[i][i+1] = 1
    for gap in range(3, n+1):
        for i in range(n-gap+1):
            j = i + gap - 1
            if arr[i] == arr[j]:
                dp[i][j] = 1
                for k in range(i, j):
                    dp[i][j] = max(dp[i][j], dp[i][k] + dp[k+1][j] + 1)
    return dp[0][n-1]

