def log_b(x, b):

    """
    Return the discrete logarithm of x in the base b.
    """
    if x == 0:
        return 0
    elif x == 1:
        return 1
    elif x == b:
        return 0
    elif x == b**2:
        return 1
    elif x == b**3:
        return 2
