def isPalindrome(string):

    """
    :type s: str
    :rtype: bool
    """
    if not string:
        return True
    if len(string) == 1:
        return True
    if len(string) == 2:
        return string[0] == string[1]
    if len(string) == 3:
        return string[0] == string[2] and string[0] == string[1]
    if len(string) % 2 == 0:
        return string[0] == string[-1] and isPalindrome(string[1:-1])
    else:
        return string[0] == string[-1] and isPalindrome(string[1:])

