def postfix_evaluation(expr):

    """
    Evaluate a given expression using postfix evaluation.
    """
    stack = []
    for token in expr:
        if token in '+-*/':
            b = stack.pop()
            a = stack.pop()
            if token == '+':
                stack.append(a + b)
            elif token == '-':
                stack.append(a - b)
            elif token == '*':
                stack.append(a * b)
            elif token == '/':
                stack.append(a / b)
        else:
            stack.append(int(token))
    return stack.pop()

